const pins={1:1,2:2,5:5,6:6,7:7};let htmlPwm="";for(const[key,value]of Object.entries(pins))htmlPwm+='<label><div class="label-name">'+pins[key]+'</div><input name="'+key+'" onchange="sendPwmStatus(this)"  type="range" value="0" min="0" max="1023" step="5"  class="range-slider" id="'+key+'"> <span class="badge" id="'+key+'BId">0</span></label>';function sendPwmStatus(e){document.getElementById(e.getAttribute("id")+"BId").innerHTML=e.value;let n=document.getElementsByTagName("input"),t=["clock=500"];Object.keys(n).forEach(function(e){t.push(n[e].name+"="+n[e].value)}),sendPwmRequest(t.join("&"))}function sendPwmRequest(e){let n=new XMLHttpRequest;n.onreadystatechange=function(){n.readyState==XMLHttpRequest.DONE&&console.log(n.responseText)},n.open("GET","/gpio-pwm.lc?"+e,!0),n.send(null)}function loadSettings(){let e=new XMLHttpRequest;e.onreadystatechange=function(){if(e.readyState==XMLHttpRequest.DONE)try{let n=JSON.parse(e.response);Object.keys(n).forEach(function(e){let t=document.getElementById(e);t&&(t.value=Number(n[e]),document.getElementById(e+"BId").innerHTML=Number(n[e]))})}catch(t){console.log(t)}},e.open("GET","/json/pwm-action.json",!0),e.send(null)}document.getElementById("pwmBlock").innerHTML=htmlPwm,setTimeout(loadSettings,500);